import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { TaskService } from 'src/services/task.service'; 

@Component({
  selector: 'app-create-new-task',
  templateUrl: './create-new-task.component.html',
  styleUrls: ['./create-new-task.component.css']
})
export class CreateNewTaskComponent {
  constructor(private taskService: TaskService, private router: Router, private http: HttpClient) {}

  onSubmit(form: NgForm) {
    if (form.valid) {
      const task = {
        id: form.value.title,
        startTime: form.value.startDate,
        endTime: form.value.endDate,
        duration: form.value.duration,
        // repeat: form.value.repeat
      };

      this.http.post('http://localhost:8080/api/tasks', task)
    .subscribe(
        response => console.log('Task created:', response),
        error => console.error('Error creating task:', error)
    );
    }
  }
}
