import { Component, OnInit } from '@angular/core';

interface Task {
  id: string;
  startTime: string;
  endTime: string;
}

interface Day {
  date: Date;
  isToday: boolean;
  tasks: Task[];
}

@Component({
  selector: 'app-calender',
  templateUrl: './calender.component.html',
  styleUrls: ['./calender.component.css'],
})
export class CalendarComponent implements OnInit {
  daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  months = [
    'January', 'February', 'March', 'April', 'May', 'June', 'July', 'August',
    'September', 'October', 'November', 'December'
  ];

  currentMonth = new Date().getMonth();
  currentYear = new Date().getFullYear();
  calendar: Day[][] = [];

  showPopup = false;
  selectedDay: Day | null = null;

  tasks: Task[] = [
    { id: '1', startTime: '2024-09-02 14:00', endTime: '2024-09-02 16:00' },
    { id: '2', startTime: '2024-09-16 09:00', endTime: '2024-09-16 10:00' }
  ];

  ngOnInit() {
    this.generateCalendar(this.currentMonth, this.currentYear);
  }

  generateCalendar(month: number, year: number) {
    const firstDay = new Date(year, month, 1).getDay();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    const today = new Date();
    const calendar: Day[][] = [];
    let week: Day[] = [];

    for (let i = 0; i < firstDay; i++) {
      week.push({ date: new Date(year, month, -firstDay + i + 1), isToday: false, tasks: [] });
    }

    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(year, month, day);
      const isToday = date.toDateString() === today.toDateString();
      const tasks = this.tasks.filter(task => new Date(task.startTime).toDateString() === date.toDateString());

      week.push({ date, isToday, tasks });

      if (week.length === 7) {
        calendar.push(week);
        week = [];
      }
    }

    if (week.length > 0) {
      calendar.push(week);
    }

    this.calendar = calendar;
  }

  previousMonth() {
    if (this.currentMonth === 0) {
      this.currentMonth = 11;
      this.currentYear--;
    } else {
      this.currentMonth--;
    }
    this.generateCalendar(this.currentMonth, this.currentYear);
  }

  nextMonth() {
    if (this.currentMonth === 11) {
      this.currentMonth = 0;
      this.currentYear++;
    } else {
      this.currentMonth++;
    }
    this.generateCalendar(this.currentMonth, this.currentYear);
  }

  showTaskPopup(day: Day) {
    this.selectedDay = day;
    this.showPopup = true;
  }

  closePopup() {
    this.showPopup = false;
  }
}

export class CalenderComponent implements OnInit {
  currentMonth: Date = new Date();
  calendar: Day[][] = [];
  selectedDay: Day | null = null;

  // constructor(private modalService: NgbModal) {}

  ngOnInit(): void {
    this.generateCalendar();
  }

  generateCalendar(): void {
    const startOfMonth = new Date(
      this.currentMonth.getFullYear(),
      this.currentMonth.getMonth(),
      1
    );
    const endOfMonth = new Date(
      this.currentMonth.getFullYear(),
      this.currentMonth.getMonth() + 1,
      0
    );

    const calendar: Day[][] = [];
    let week: Day[] = [];
    let currentDate = new Date(startOfMonth);
    currentDate.setDate(currentDate.getDate() - currentDate.getDay());

    while (currentDate <= endOfMonth || currentDate.getDay() !== 0) {
      week.push({
        date: new Date(currentDate),
        tasks: this.getTasksForDay(currentDate), // fetch tasks for this day
      });

      if (currentDate.getDay() === 6) {
        calendar.push(week);
        week = [];
      }

      currentDate.setDate(currentDate.getDate() + 1);
    }

    this.calendar = calendar;
  }

  getTasksForDay(date: Date): Task[] {
    // Dummy tasks, replace with actual task data
    const tasks: Task[] = [
      { title: 'Task 1', startTime: '10:00', endTime: '11:00' },
      { title: 'Task 2', startTime: '12:00', endTime: '13:00' },
    ];

    // For simplicity, we'll assume all tasks occur on the 15th of the month
    if (date.getDate() === 15) {
      return tasks;
    }

    return [];
  }

  prevMonth(): void {
    this.currentMonth.setMonth(this.currentMonth.getMonth() - 1);
    this.generateCalendar();
  }

  nextMonth(): void {
    this.currentMonth.setMonth(this.currentMonth.getMonth() + 1);
    this.generateCalendar();
  }

  isToday(date: Day): boolean {
    const today = new Date();
    return (
      today.getDate() === date.date.getDate() &&
      today.getMonth() === date.date.getMonth() &&
      today.getFullYear() === date.date.getFullYear()
    );
  }

  openDayModal(day: Day): void {
    this.selectedDay = day;
    // this.modalService.open(document.getElementById('taskModal'), {
    //   ariaLabelledBy: 'taskModalLabel',
    // });
  }
}
