import { ITask } from 'src/interfaces/ITask';
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Co
@Component({
    selector: 'app-tasks',
    templateUrl: './tasks.component.html', // Correct relative path to HTML file
    styleUrls: ['./tasks.component.css']  // Correct relative path to CSS file
  })
export class TaskService {
  private apiUrl = 'http://localhost:8080/api/tasks'; // Change to your backend API URL

  constructor(private http: HttpClient) {}

  getTasks(): Observable<any[]> {
    return this.http.get<any[]>(this.apiUrl);
  }

  getTaskById(id: number): Observable<any> {
    return this.http.get<any>(`${this.apiUrl}/${id}`);
  }

  createTask(taskData: any): Observable<any> {
    return this.http.post<any>(this.apiUrl, taskData);
  }

  updateTask(id: number, taskData: any): Observable<any> {
    return this.http.put<any>(`${this.apiUrl}/${id}`, taskData);
  }
}
