import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { ITask } from 'src/interfaces/ITask';

@Injectable({
  providedIn: 'root',
})
export class TaskService {
  private apiUrl = 'http://localhost:8080/api/tasks';
  private counter = 0; 

  constructor(private http: HttpClient) {}

   generateTaskId(): string {
    const letters = 'ABC';
    const idNumber = this.counter++;
    const formattedIdNumber = idNumber.toString().padStart(4, '0');
    return `${letters}${formattedIdNumber}`;
  }

  getTasks(): Observable<ITask[]> {
    return this.http.get<ITask[]>(this.apiUrl);
  }

  getTaskById(id: string) {
    return this.http.get<ITask>(`${this.apiUrl}/${id}`);
  }

  createTask(taskData: ITask) {
    return this.http.post(this.apiUrl, taskData);
  }

  updateTask(id: string, taskData: ITask) {
    return this.http.put<ITask>(`${this.apiUrl}/${id}`, taskData);
  }
}
