import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { ITask } from 'src/interfaces/ITask';
import { TaskService } from 'src/services/task.service';

@Component({
  selector: 'app-create-new-task',
  templateUrl: './create-new-task.component.html',
  styleUrls: ['./create-new-task.component.css'],
})
export class CreateNewTaskComponent {
  constructor(private taskService: TaskService, private router: Router) {}
  formData = {
    startDate: new Date(),
    endDate: new Date(),
    startHour: 0,
    startMinute: 0,
    duration: 0,
    frequency: 'daily',
    repeatInterval: 1,
  };

  onSubmit(form: NgForm) {
    if (form.valid) {
      const {
        startDate,
        endDate,
        startHour,
        startMinute,
        duration,
        frequency,
        repeatInterval,
      } = this.formData;
      console.log('hello', form);
      const tasks = this.generateTasks(
        startDate,
        endDate,
        startHour,
        startMinute,
        duration,
        repeatEvery
      );

      // this.taskService.createTask(task).subscribe({
      //   next: (response) => {
      //     console.log('Task created', response);
      //     this.router.navigate(['/tasks']);
      //   },
      //   error: (error) => console.error('Error creating task', error),
      // });
    }
  }

  getEndTime(startTime: Date, duration: number): Date {
    const result = new Date(startTime);
    result.setMinutes(result.getMinutes() + duration);
    return result;
  }
  addDays(date: Date, days: number): Date {
    const result = new Date(date);
    result.setDate(result.getDate() + days);
    return result;
  }
  isBefore(date1: Date, date2: Date): boolean {
    return date1 < date2;
  }

  generateTasks(
    startDate: Date,
    endDate: Date,
    startHour: number,
    startMinute: number,
    duration: number,
    repeatEvery: number
  ) {
    const tasks: ITask[] = [];
    let currentDate = startDate;
    console.log('startDate', currentDate);
    const startTime = new Date(
      `${String(startHour).padStart(2, '0')}:${String(startMinute).padStart(
        2,
        '0'
      )}`
    );

    const endTime = this.getEndTime(startTime, duration);

    while (
      this.isBefore(currentDate, endDate) ||
      currentDate.toDateString() === endDate.toDateString()
    ) {
      tasks.push({
        id: `task${new Date().getTime()}`, // Generate a unique ID
        startTime: startTime,
        endTime: endTime,
        duration: duration,
      });
      currentDate = this.addDays(currentDate, repeatEvery);
    }
  }
}
    <div class="container mt-5">
      <h2>Start time and Occurrences</h2>
      <form #taskForm="ngForm" (ngSubmit)="onSubmit(taskForm)">
        <div class="form-group">
          <div class="row mt-3">
            <label for="startDate">Enter a date range</label>
            <div class="col">
              <div class="input-group">
                <input
                  type="date"
                  id="startDate"
                  name="startDate"
                  [(ngModel)]="formData.startDate"
                  class="form-control"
                />
              </div>
            </div>
            <div class="col">
              <input
                type="date"
                id="endDate"
                name="endDate"
                [(ngModel)]="formData.endDate"
                class="form-control"
              />
            </div>
            <div class="col">
              <div class="input-group">
                <input
                  type="number"
                  id="startHour"
                  name="startHour"
                  [(ngModel)]="formData.startHour"
                  class="form-control"
                  placeholder="HH"
                  min="0"
                  max="23"
                />
                <input
                  type="number"
                  id="startMinute"
                  name="startMinute"
                  [(ngModel)]="formData.startMinute"
                  class="form-control"
                  placeholder="MM"
                  min="0"
                  max="59"
                />
              </div>
            </div>
          </div>
        </div>
        <div class="form-group">
          <label for="duration">Duration</label>
          <div class="input-group">
            <input
              type="number"
              id="duration"
              name="duration"
              [(ngModel)]="formData.duration"
              class="form-control"
              min="1"
              required
            />
            <div class="input-group-append">
              <span class="input-group-text">minutes</span>
            </div>
          </div>
        </div>

        <div class="form-group">
          <label class="me-4">Frequency</label>
          <div class="form-check ml-3">
            <input
              type="radio"
              id="daily"
              name="frequency"
              [(ngModel)]="formData.frequency"
              value="daily"
              class="form-check-input"
              checked
            />
            <label for="daily" class="form-check-label">Daily</label>
          </div>
          <div class="form-check ml-3">
            <input
              type="radio"
              id="weekly"
              name="frequency"
              [(ngModel)]="formData.frequency"
              value="weekly"
              class="form-check-input"
            />
            <label for="weekly" class="form-check-label">Weekly</label>
          </div>
        </div>

        <div class="form-group">
          <label for="repeatInterval">Repeat every</label>
          <div class="input-group">
            <input
              type="number"
              id="repeatInterval"
              name="repeatInterval"
              [(ngModel)]="formData.repeatInterval"
              class="form-control"
              min="1"
            />
            <div class="input-group-append">
              <span class="input-group-text">days</span>
            </div>
          </div>
        </div>

        <button type="submit" class="btn btn-primary mt-4">Submit</button>
      </form>
    </div>

    <script src="https://code.jquery.com/jquery-3.5.1.slim.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.9.2/dist/umd/popper.min.js"></script>
